function findShortestPath(maze) {
    let visited = new Set();

    let queue = [];
    queue.push({
        row: 0,
        col: 0
    });

    let minLength = Number.POSITIVE_INFINITY;

    while (queue.length > 0) {
        let node = queue.shift();
        let row = node.row;
        let col = node.col;
        let length = node.length;

        // Reached the end
        if (row === maze.length - 1 && col === maze[0].length - 1) {
            minLength = Math.min(minLength, length);
        }

        // Add neighbors
        if (row - 1 >= 0 && maze[row - 1][col] === 0
            && !visited.has(row - 1 + '_' + col)) {
            queue.push({
                row: row - 1,
                col: col,
                length: length + 1
            });
            visited.add(row - 1 + '_' + col);
        }

        if (row + 1 < maze.length && maze[row + 1][col] === 0
            && !visited.has(row + 1 + '_' + col)) {
            queue.push({
                row: row + 1,
                col: col,
                length: length + 1
            });
            visited.add(row + 1 + '_' + col);
        }

        if (col - 1 >= 0 && maze[row][col - 1] === 0
            && !visited.has(row + '_' + (col - 1))) {
            queue.push({
                row: row,
                col: col - 1,
                length: length + 1
            });
            visited.add(row + '_' + (col - 1));
        }

        if (col + 1 < maze[0].length && maze[row][col + 1] === 0
            && !visited.has(row + '_' + (col + 1))) {
            queue.push({
                row: row,
                col: col + 1,
                length: length + 1
            });
            visited.add(row + '_' + (col + 1));
        }
    }

    return minLength;
}

