function anagramCheck(str1, str2) {
  // Remove whitespace, convert strings to lowercase, and split into arrays
  let arr1 = str1.replace(/\s/g, '').toLowerCase().split('');
  let arr2 = str2.replace(/\s/g, '').toLowerCase().split('');

  // If the arrays are different lengths, they cannot be anagrams
  if (arr1.length !== arr2.length) {
    return false;
  }

  // Sort both arrays, and check if they are the same
  let sortedArr1 = arr1.sort();
  let sortedArr2 = arr2.sort();

  for (let i=0; i<sortedArr1.length; i++) {
    if (sortedArr1[i] !== sortedArr2[i]) {
      return false;
    }
  }
  return true;
}

