function arrayToCSV(data) {
  let csv = '';
  const headers = Object.keys(data[0]);
  csv += headers.join(',') + '\n';
  data.forEach((item) => {
    let row = '';
    Object.values(item).forEach((value) => {
      row += `"${value}",`;
    });
    row = row.slice(0, -1);
    csv += `${row}\n`;
  });
  return csv;
}

