function deleteDuplicates(str) {  
  let result = ""; 
  let frequency = {}; 

  // creating a map of all characters with their frequency in the string 
  for (let i = 0; i < str.length; i++) { 
    let currentChar = str.charAt(i); 
    if (frequency[currentChar]) { 
      frequency[currentChar]++; 
    } else { 
      frequency[currentChar] = 1; 
    } 
  } 

  // adding only those characters which have frequency greater than one 
  for (let i = 0; i < str.length; i++) { 
    let currentChar = str.charAt(i); 
    if (frequency[currentChar] == 1) { 
      result += str.charAt(i); 
    } 
  } 

  return result; 
}

