function validateCreditCardNumber(number) {
    // remove any spaces or dashes
    number = number.replace(/\s/g, ""); // remove any spaces
    number = number.replace(/-/g, ""); // remove any dashes
    // check for valid credit card length
    if (number.length != 16) {
        return false;
    }
    // check that each number is a valid number
    for (let i = 0; i < 15; i++) {
        let n = parseInt(number[i]);
        if (isNaN(n)) {
            return false;
        }
    }
    // validate the credit card
    let sum = 0;
    for (let i = 0; i < 15; i++) {
        let n = parseInt(number[i]);
        if (i % 2 == 0) {
            n *= 2;
            if (n > 9) { 
                n = (n % 10) + 1;
            }
        }
        sum += n;
    }
    let last = parseInt(number[15]);
    let check = 10 - (sum % 10);
    return last == check;
}

