function areaOfTriangle(a, b, c) {
    // Calculate the semi-perimeter of the triangle
    var s = (a + b + c) / 2;

    // Calculate the area of triangle using Heron's Formula
    var area =  Math.sqrt(s * (s - a) * (s - b) * (s - c));

