"""
Design a module in JavaScript that sorts an array of integers starting from the smallest number.
"""
// Create the sorting function
function sortArray(array){
  // Keep track of the index of the minimum value.
  let index;

  //loop through the array starting from the beginning.
  for(let i = 0; i < array.length; i++){
    //set the min value to the current i value.
    let min = array[i];
    //set the index to i
    index = i;

    //loop through the array starting from i + 1.
    for(let j = i + 1; j < array.length; j++){
      //if the value of array[j] is less then the min value
      //then set the min to this value and set the index to j
      if (array[j] < min){
        min = array[j];
        index = j;
      }
    }

    //if there was a new min then swap it at the current i position. 
    if(i != index){
      let temp = array[i];
      array[i] = array[index];
      array[index] = temp;
    }
  }

  //return the sorted array
  return array;
}

