function quadraticEquation(a, b, c) {
    let result;
    let delta = (b * b) - (4 * a * c);
    if (delta > 0) {
      result = {
        root1: (-b + Math.sqrt(delta)) / (2 * a),
        root2: (-b - Math.sqrt(delta)) / (2 * a)
      };
    }
    else if (delta == 0) {
      result = {
        root1: (-b + Math.sqrt(delta)) / (2 * a)
      };
    }
    else {
      result = {
        root1: 'cannot calculate the roots'
      };
    }
    return result;
  }

function findPerfectNumbers(n) {
  var perfects = [];

  for(var i = 1; i<=n; i++){
    var divisorsSum = 0;
    for(var j = 1; j<i; j++){
      if(i % j == 0){
        divisorsSum += j;
      }
    }
    if(divisorsSum == i){
      perfects.push(i);
    }
  }

  return perfects;
}

