def find_magic_in_file(filename):
    """
    Search the file for any magic incantations.

    :param filename: file to search
    :returns: a tuple containing the spell and then maybe some extra words (or None if no magic present)
    """
    with open(filename, "rt", encoding="utf-8") as f:
        for line in f:
            if line.startswith("#"):
                comment = line[1:].strip()
                if comment.startswith("~~~~* ") or comment.startswith("----* ") or comment.startswith("====* "):
                    spell = comment[5:].strip()
                    return tuple(spell.split())
            else:
                break
    return None

def updateEditor( self ):
        """
        Updates the editor based on the current selection.
        """

        # assignt the rule operators to the choice list
        rule        = self.currentRule()
        operator    = self.currentOperator()
        widget      = self.uiWidgetAREA.widget()
        editorType  = None
        text        = ''

        if ( rule ):
            editorType = rule.editorType(operator)

        # no change in types
        if ( widget and editorType and type(widget) == editorType ):
            return

        elif ( widget ):
            if ( type(widget) != QWidget ):
                text = widget.text()

            widget.setParent(None)
            widget.deleteLater()

        self.uiWidgetAREA.setWidget(None)

        # create the new editor
        if ( editorType ):
            widget = editorType(self)

            if ( isinstance(widget, QLineEdit) ):
                terms  = rule.completionTerms()
                if ( not terms ):
                    qwidget = self.queryBuilderWidget()
                    if ( qwidget ):
                        terms = qwidget.completionTerms()

                if ( terms ):
